private function checar_envio_produto($product_id) {
	$checar_envio = $this->db->query('SELECT shipping FROM `' . DB_PREFIX . 'product` WHERE `product_id` = "'.(int)$product_id.'"');
	return $checar_envio->row['shipping'];
    }
